// WF106: Schema Auto-Builder - Generate SQL from JSON
const schema = $input.first().json.body;

// Validate required fields
if (!schema.version || !schema.description || !schema.tables) {
  throw new Error('Missing required fields: version, description, tables');
}

// Helper: Convert JSON column definition to SQL
function columnToSQL(col) {
  let sql = `${col.name} ${col.type}`;
  if (col.primary_key) sql += ' PRIMARY KEY';
  if (col.default) sql += ` DEFAULT ${col.default}`;
  if (col.not_null) sql += ' NOT NULL';
  if (col.unique) sql += ' UNIQUE';
  return sql;
}

// Generate CREATE TABLE statements
const sqlStatements = [];
const tableNames = [];

for (const table of schema.tables) {
  tableNames.push(table.name);
  const columns = table.columns.map(col => columnToSQL(col)).join(',\n    ');
  let sql = `CREATE TABLE IF NOT EXISTS public.${table.name} (\n    ${columns}`;

  if (table.indexes && table.indexes.length > 0) {
    sql += '\n);\n\n';
    for (const idx of table.indexes) {
      sql += `CREATE INDEX IF NOT EXISTS ${idx.name} ON public.${table.name}(${idx.columns.join(', ')});\n`;
    }
  } else {
    sql += '\n);';
  }
  sqlStatements.push(sql);
}

const fullSQL = sqlStatements.join('\n\n');
const checksum = require('crypto').createHash('sha256').update(fullSQL).digest('hex');

return {
  version: schema.version,
  description: schema.description,
  sql: fullSQL,
  checksum: checksum,
  table_count: schema.tables.length,
  table_names: tableNames.join(', '),
  timestamp: new Date().toISOString()
};
